import{_ as t,c as o,o as r,j as e,a}from"./chunks/framework.4SZxrD75.js";const x=JSON.parse('{"title":"田小娥还是李沁","description":"","frontmatter":{},"headers":[],"relativePath":"movie/田小娥还是李沁.md","filePath":"movie/田小娥还是李沁.md"}'),s={name:"movie/田小娥还是李沁.md"},_=e("h1",{id:"田小娥还是李沁",tabindex:"-1"},[a("田小娥还是李沁 "),e("a",{class:"header-anchor",href:"#田小娥还是李沁","aria-label":'Permalink to "田小娥还是李沁"'},"​")],-1),n=e("h4",{id:"_2017-06-22",tabindex:"-1"},[a("2017/06/22 "),e("a",{class:"header-anchor",href:"#_2017-06-22","aria-label":'Permalink to "2017/06/22"'},"​")],-1),c=e("p",null,[a("最近看了白鹿原，看完之后留给大家争论最多的就是田小娥了。虽然田小娥的篇幅不是最大的，但肯定就是最受偏爱的。陈忠实先生本人对其就是偏爱的"),e("br"),a(" 《白鹿原》可能就是最近国产电视剧中评分较高的一部，加之没有其他什么剧可以看，就在网上看了一些。看了开头就能感受的出来，这至少是一部认真的，有态度的电视剧，而且当初播出也是一波三折。说是原著的很多方面在当下还是相当的敏感话题，所以注定是有着不少的改动，开播后还临时删减了一遍，好像由 85 集砍到了 77 集。《白鹿原》主要描述关中地区一个小小的原上在上个世纪动荡的 50 年发生的种种变迁。这类题材感觉可以和《平凡的世界》一起品鉴。文中的人物都有鲜明的性格特征和时代印记。娥儿姐实在是太美了，当然可能只是李沁太美了，没看过影版的，就不作比较了。剧中一幕幕的悲剧，每个人看的时候基本都会说，这是时代的悲哀。我们总是找不到一个合适的原因，或者说是我们不愿寻找。然后就轻轻松松把那顶帽子扣给了时代。可是一个时代过去后，我们常常会发现，似乎我们和上一个时代并没有什么差别，形形色色的人物都还在，白孝文还是适合从政的那批人。这个时代还是会有这个时代的田小娥，将来也会有属于我们的《白鹿原》，我们常常对时代反思，却很少对自己反思，毕竟时代是每一个人参与的结果。我们真的会容易学会心安理得的生活。沧海桑田，不变的是人性。"),e("br"),a(" 扯了一些没用的，说说最近的事情把，上个月去了一趟桂林，之后端午回家了一趟，然后去西安考了驾照。至此折腾一番，这两天就离职了，接下来，打算在这待一阵，然后出去走走，再回家休息一下。再出发。。。")],-1),i=[_,n,c];function d(l,h,m,p,f,b){return r(),o("div",null,i)}const v=t(s,[["render",d]]);export{x as __pageData,v as default};
